CREATE OR REPLACE FUNCTION vipe_obter_se_agrupa_item ( ie_agrupar_p text, ie_tipo_item_p text, nr_atendimento_p bigint, nr_seq_horario_p bigint, nr_prescricao_p bigint, qt_dose_p bigint, cd_intervalo_p text, dt_horario_p timestamp, cd_material_p bigint, nr_seq_material_p bigint, dt_inicio_prescr_p timestamp, dt_validade_prescr_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_agrupar_w	varchar(1) := 'S';
cont_w		bigint;
dt_inicio_w	timestamp;
dt_fim_w	timestamp;

BEGIN

if (ie_agrupar_p = 'N') then

	if (ie_tipo_item_p = 'M') or (ie_tipo_item_p = 'MAT') or (ie_tipo_item_p = 'S') or (ie_tipo_item_p = 'SNE') then

		dt_inicio_w	:= dt_inicio_prescr_p - 2;
		dt_fim_w	:= dt_validade_prescr_p + 2;

		select	count(*)
		into STRICT	cont_w
		from	prescr_medica b,
			prescr_material c,
			prescr_mat_hor a
		where	a.nr_prescricao		= b.nr_prescricao
		and	c.nr_prescricao		= a.nr_prescricao
		and	a.nr_seq_material	= c.nr_sequencia
		and	b.nr_atendimento	= nr_atendimento_p
		and	a.nr_sequencia		<> nr_seq_horario_p
		and	((a.nr_prescricao   	<> nr_prescricao_p) or (c.nr_sequencia	<> nr_seq_material_p))
		and	c.qt_dose		= qt_dose_p
		and	coalesce(b.dt_suspensao::text, '') = ''
		and	coalesce(c.dt_suspensao::text, '') = ''
		and	c.cd_intervalo		= cd_intervalo_p
		and	coalesce(a.dt_suspensao::text, '') = ''
		and 	b.dt_validade_prescr between dt_inicio_w and dt_fim_w
		and	a.dt_horario		in (SELECT dt_horario
						from	prescr_mat_hor
						where	nr_prescricao   = nr_prescricao_p
						and 	nr_seq_material = nr_seq_material_p)
		and	a.cd_material		= cd_material_p;

		if (cont_w > 0) then
			ie_agrupar_w	:= 'N';
		end if;
	end if;
end if;

return	ie_agrupar_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
