CREATE OR REPLACE FUNCTION visualizacao_conv_glosa (cd_convenio_p bigint) RETURNS varchar AS $body$
DECLARE

				 
vl_param_w 		varchar(1);
verifica_estab_conv_w 	bigint;
valor_w 		varchar(1):= 'N';


BEGIN 
 
vl_param_w := obter_param_usuario(916, 1135, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, vl_param_w);
 
 
 
if (vl_param_w = 'N') then 
	begin 
		select CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
		into STRICT valor_w 
		from convenio_estabelecimento 
		where cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento 
				and cd_convenio = cd_convenio_p;
	end;
elsif (vl_param_w = 'S') then 
	valor_w := 'S';
end if;	
return valor_w;
 
end;
