CREATE OR REPLACE FUNCTION visualizar_convenio_regra ( cd_estabelecimento_p bigint, cd_perfil_p bigint, nm_usuario_p text, cd_convenio_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1) := 'N';
qt_conv_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_conv_w
from	regra_liberacao_convenio;

if (coalesce(qt_conv_w,0) = 0) then
	ds_retorno_w := 'S';
else
	select	count(*)
	into STRICT	qt_conv_w
	from	regra_liberacao_convenio
	where	cd_convenio						= cd_convenio_p
	and 	coalesce(cd_estabelecimento, coalesce(cd_estabelecimento_p,0)) 	= coalesce(cd_estabelecimento_p,0)
	and 	coalesce(cd_perfil, coalesce(cd_perfil_p,0)) 			= coalesce(cd_perfil_p,0)
	and 	coalesce(nm_usuario_lib, coalesce(nm_usuario_p,'X'))		= coalesce(nm_usuario_p,'X');

	if (qt_conv_w > 0) then
		ds_retorno_w 	:= 'S';
	else
		ds_retorno_w	:= 'N';
	end if;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
