CREATE OR REPLACE FUNCTION visual_coluna_painel_agenda ( nr_seq_objeto_p bigint, nm_campo_base_p text, cd_perfil_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_exibir_atributo_w	varchar(1);
ie_configuravel_w	varchar(1);


C01 CURSOR FOR
	SELECT	max(a.ie_exibir_atributo)
	into STRICT	ie_exibir_atributo_w
	from	wcp_regra_atributo a,
		dic_objeto b
	where	a.nr_seq_atributo  	 		= b.nr_sequencia
	and	a.nr_seq_wcpanel  			= nr_seq_objeto_p
	and	b.nm_campo_base   		= nm_campo_base_p
	and	coalesce(nm_usuario_regra,nm_usuario_p) 	= nm_usuario_p
	and	coalesce(cd_perfil,cd_perfil_p) 		= cd_perfil_p
	order by
	coalesce(nm_usuario_regra,'AAAAAAAAAAAAAA'),
	coalesce(cd_perfil,0);


BEGIN
/*
select	nvl(max(a.ie_exibir_atributo),'S')
into	ie_exibir_atributo_w
from	wcp_regra_atributo a,
	dic_objeto b
where	a.nr_seq_atributo  	 		= b.nr_sequencia
and	a.nr_seq_wcpanel  			= nr_seq_objeto_p
and	b.nm_campo_base   			= nm_campo_base_p
and	nvl(nm_usuario_regra,nm_usuario_p) 	= nm_usuario_p
and	nvl(cd_perfil,cd_perfil_p) 		= cd_perfil_p;

*/
open C01;
loop
fetch C01 into
	ie_exibir_atributo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

select	max(ie_configuravel)
into STRICT 	ie_configuravel_w
from   	dic_objeto
where	nr_seq_obj_sup = nr_seq_objeto_p
and  	nm_campo_base  = nm_campo_base_p
and	ie_tipo_objeto = 'AC';

if (ie_configuravel_w = 'R') and (coalesce(ie_exibir_atributo_w::text, '') = '') then
   ie_exibir_atributo_w := 'N';
else
   ie_exibir_atributo_w := coalesce(ie_exibir_atributo_w, 'S');
end if;

return	coalesce(ie_exibir_atributo_w,'S');

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
