CREATE OR REPLACE FUNCTION vtrp_obter_cod_mat_tiss (cd_estabelecimento_p bigint, cd_material_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

	 
	 
ds_retorno_w			varchar(255) := '';
cd_material_brasindice_w	varchar(255) := '';
cd_laboratorio_w		varchar(255) := '';
cd_medicamento_w		varchar(255) := '';
cd_apresentacao_w		varchar(255) := '';


BEGIN 
 
select	max(cd_material_convenio) 
into STRICT	ds_retorno_w 
from	conversao_material_convenio c 
where	c.cd_convenio = Obter_Convenio_Atendimento(nr_atendimento_p) 
and	c.cd_material = cd_material_p;
	 
 
if (coalesce(ds_retorno_w::text, '') = '') then 
	cd_material_brasindice_w	:= obter_codigo_brasindice(cd_estabelecimento_p, cd_material_p, clock_timestamp(), Obter_Convenio_Atendimento(nr_atendimento_p));
	cd_laboratorio_w		:= substr(cd_material_brasindice_w,1,3);
	cd_medicamento_w		:= substr(cd_material_brasindice_w,5,5);
	cd_apresentacao_w		:= substr(cd_material_brasindice_w,11,4);
	ds_retorno_w			:= tiss_obter_cod_brasindice(cd_apresentacao_w, cd_laboratorio_w, cd_medicamento_w);
	 
end if;
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
