CREATE OR REPLACE FUNCTION wehr_obter_nome_coluna_grid ( ds_column_name_p text, nm_usuario_p text ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(8);
ds_comando_w		varchar(255);


BEGIN

	ds_comando_w :=
	'select	to_char(to_date('||ds_column_name_p||'), ''dd/mm/yy'') '||
	'from	w_ehr_reg_elemento a '||
	'where	ie_ordem = -3 '||
	'and	nm_usuario = :nm_usuario';

	ds_retorno_w := Obter_valor_Dinamico_char_bv(ds_comando_w, 'nm_usuario='||nm_usuario_p||';', ds_retorno_w);

return	ds_retorno_w;

end;
