SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION wheb_obter_perc_os_atraso (dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


qt_retorno_w		double precision;

BEGIN

if (trunc(dt_referencia_p,'mm') = trunc(clock_timestamp(),'mm')) then

	select (dividir(sum(CASE WHEN obter_se_data_periodo(trunc(a.dt_ordem_servico, 'dd'), to_date('01/01/2007','dd/mm/yyyy'), trunc(clock_timestamp() - interval '30 days','dd'))='S' THEN  1  ELSE 0 END ), count(*))*100)
	into STRICT	qt_retorno_w
	from	gerencia_wheb c,
		grupo_desenvolvimento b,
		man_ordem_servico  a
	where	b.nr_seq_gerencia	= c.nr_sequencia
	and	a.nr_seq_grupo_des    = b.nr_sequencia
	--and	(substr(obter_se_os_pend_cliente(a.nr_sequencia),1,1) = 'N')
	and (substr(obter_se_os_desenv(a.nr_sequencia),1,1) = 'S')
	and	substr(obter_se_ordem_vinc_projeto2(a.nr_sequencia),1,1) = 'N'
	and (a.ie_status_ordem <> 3)
	and	c.nr_sequencia <> 5;

else
	select (dividir(sum(CASE WHEN obter_se_data_periodo(trunc(a.dt_ordem_servico, 'dd'), to_date('01/01/2007','dd/mm/yyyy'), trunc(dt_referencia_p -30,'dd'))='S' THEN  1  ELSE 0 END ), count(*))*100)
	into STRICT	qt_retorno_w
	from	gerencia_wheb c,
		grupo_desenvolvimento b,
		man_ordem_servico  a
	where	b.nr_seq_gerencia	= c.nr_sequencia
	and	a.nr_seq_grupo_des    = b.nr_sequencia
	--and	(substr(obter_se_os_pend_cliente_data(a.nr_sequencia, dt_referencia_p),1,1) = 'N')
	and (substr(obter_se_os_desenv_data(a.nr_sequencia, dt_referencia_p),1,1) = 'S')
	and	substr(obter_se_ordem_vinc_projeto2(a.nr_sequencia),1,1) = 'N'
	and	c.nr_sequencia <> 5;

end if;

return	trunc(qt_retorno_w);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
