CREATE OR REPLACE FUNCTION wheb_obter_satisf_sup ( dt_inicial_p timestamp, dt_final_p timestamp) RETURNS bigint AS $body$
DECLARE


pr_retorno_w		double precision;
dt_inicial_w		timestamp;
dt_final_w		timestamp;
qt_total_os_w		bigint;
qt_satisf_os_w		bigint;


BEGIN

dt_inicial_w	:= dt_inicial_p;
dt_final_w	:= dt_final_p;

select	count(*)
into STRICT	qt_satisf_os_w
from	grupo_suporte b,
	man_ordem_servico a
where	a.dt_fim_real between dt_inicial_w and dt_final_w
and     	a.ie_grau_satisfacao in ('B','O') -- Retirado o E (Excelente)
and	a.nr_seq_grupo_sup = b.nr_sequencia
and	b.nr_seq_gerencia_sup in (5,58)
and	coalesce(a.ie_grau_satisfacao, 'N') != 'N' -- Adicionado verificacao para nao considerar NAO SE APLICA e os EM BRANCO
and	substr(wheb_obter_se_os_satisf_sup(a.nr_sequencia),1,1) = 'S';

select	count(*)
into STRICT	qt_total_os_w
from	grupo_suporte b,
	man_ordem_servico a
where	a.dt_fim_real between dt_inicial_w and dt_final_w
and	a.nr_seq_grupo_sup = b.nr_sequencia
and	b.nr_seq_gerencia_sup in (5,58)
and	substr(wheb_obter_se_os_satisf_sup(a.nr_sequencia),1,1) = 'S'
and	coalesce(a.ie_grau_satisfacao, 'N') != 'N' -- Adicionado verificacao para nao considerar NAO SE APLICA e os EM BRANCO
and	exists (	SELECT	1
		from	valor_dominio x
		where	x.cd_dominio	= 1197
		and	x.vl_dominio	= coalesce(a.ie_grau_satisfacao, 'N'));

pr_retorno_w	:= (dividir(qt_satisf_os_w,qt_total_os_w) * 100);

return	pr_retorno_w;

end;
