SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION wheb_obter_se_os_satisf_sup ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE



dt_liberacao_w		timestamp;
ie_classificacao_w	varchar(1);
ie_os_suporte_w		varchar(1) := 'N';


BEGIN

select	max(ie_classificacao)
into STRICT	ie_classificacao_w
from	man_ordem_servico a
where	a.nr_sequencia	= nr_sequencia_p;

if (ie_classificacao_w = 'E') then

	select	coalesce(max('S'),'N')
	into STRICT	ie_os_suporte_w
	from	usuario b,
		man_doc_erro a
	where	a.nr_seq_ordem	= nr_sequencia_p
	and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and	a.nm_usuario_nrec = b.nm_usuario
	and	b.cd_setor_atendimento	= 4;

elsif (ie_classificacao_w IS NOT NULL AND ie_classificacao_w::text <> '') then

	select	coalesce(max('S'),'N')
	into STRICT	ie_os_suporte_w
	from	man_ordem_servico c, grupo_suporte d
	where	c.nr_sequencia	= nr_sequencia_p
	and	c.nr_seq_grupo_sup = d.nr_sequencia
	and	coalesce(c.nr_seq_gerencia_insatisf,d.nr_seq_gerencia_sup) = d.nr_seq_gerencia_sup
	and (not exists (SELECT	1
				from	man_ordem_serv_estagio y,
					man_estagio_processo x
				where	x.nr_sequencia	= y.nr_seq_estagio
				and	y.nr_seq_ordem	= c.nr_sequencia
				and	x.ie_desenv	= 'S') or c.nr_seq_gerencia_insatisf =d.nr_seq_gerencia_sup);
end if;

return	ie_os_suporte_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
