SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION wl_obter_se_escala_periodo ( nr_atendimento_p bigint, ie_escala_p text, qt_tempo_p bigint, ie_evolucoes_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
qt_reg_w		bigint;
ds_comando_w		varchar(2000);
nm_tabela_w		varchar(100);
qt_tarefa_escala_w	bigint;
			

BEGIN
nm_tabela_w := obter_tabela_escala(ie_escala_p);

if (ie_evolucoes_p = 'A') then

	ds_comando_w := 'select	count(*)
			from	'||nm_tabela_w||' a
			where	a.nr_atendimento = :nr_atendimento 
			and	a.dt_liberacao is not null';
					
	select	count(*)
	into STRICT	qt_tarefa_escala_w
	from	wl_worklist
	where	nr_atendimento = nr_atendimento_p
	and	ie_escala = ie_escala_p;
					
else

	ds_comando_w := 'select	count(*)
			from	'||nm_tabela_w||' a
			where	a.nr_atendimento = :nr_atendimento 
			and	a.dt_liberacao is not null
			and	a.dt_liberacao between sysdate-'||qt_tempo_p||'/24 and sysdate';
					
	select	count(*)
	into STRICT	qt_tarefa_escala_w
	from	wl_worklist
	where	nr_atendimento = nr_atendimento_p
	and	ie_escala = ie_escala_p
	and	dt_inicial between clock_timestamp()-(qt_tempo_p/24) and clock_timestamp();

end if;

qt_reg_w := obter_valor_dinamico_bv(ds_comando_w, 'nr_atendimento=' || nr_atendimento_p, qt_reg_w);

if (qt_reg_w = 0) and (qt_tarefa_escala_w = 0) then
	ds_retorno_w := 'N';
else
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
