CREATE OR REPLACE FUNCTION wsuite_obter_query_pa (query_type_p text) RETURNS varchar AS $body$
DECLARE

return_w varchar(4000);

BEGIN
  if (query_type_p = 'PA') then
    return_w := 'select count(a.nr_atendimento) qtPacientes,' ||
                '       coalesce(sum(a.min_espera),0) totalMinEspera' ||
                '  from painel_pronto_atend_v a' ||
                ' where (a.dt_entrada between sysdate - 1 and sysdate + 1)' ||
                '   and a.dt_lib_medico is null' ||
                '   and a.dt_alta is null' ||
                '   and a.ie_status =  ''N''';
  end if;
  return return_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
