SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION wsuite_obter_usuario_info ( cd_pessoa_fisica_p text, ie_option_p text) RETURNS varchar AS $body$
DECLARE

  ds_retorno_w varchar(255);
  /* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
  Finality:
  E    Retrieve user active email id
  N    Retrieve registered user name
  ++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
BEGIN
  if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
    if (ie_option_p        = 'E') then
      begin
        SELECT COALESCE(obter_email_pf(pf.cd_pessoa_fisica), tws_get_user_email(pf.nm_usuario), x.nm_usuario_web) ds_email
        into STRICT ds_retorno_w
        FROM pessoa_fisica pf
        inner join wsuite_usuario x on pf.cd_pessoa_fisica = x.cd_pessoa_fisica
        left join compl_pessoa_fisica cpf on pf.cd_pessoa_fisica = cpf.cd_pessoa_fisica
        WHERE pf.cd_pessoa_fisica = cd_pessoa_fisica_p
        and cpf.ie_tipo_complemento = 1
        and x.ie_situacao = 'A'
        and obter_se_email_valido(x.nm_usuario_web) = 'S'  LIMIT 1;
      exception
        when no_data_found then
             return null;
      end;
    elsif (ie_option_p      = 'N') then
      begin
        select coalesce(obter_nome_pf(a.cd_pessoa_fisica), obter_desc_expressao(330784))
        into STRICT ds_retorno_w
        from wsuite_usuario a left outer join wsuite_solic_inclusao_pf b
        on b.nr_sequencia = a.nr_seq_inclusao_pf
        where (a.ie_situacao        = 'A' or b.ie_status    = 1)        
        and (trim(both a.nm_usuario_web) = trim(both cd_pessoa_fisica_p) or (trim(both b.ds_email) = trim(both cd_pessoa_fisica_p)));
        exception
        when no_data_found then
        ds_retorno_w := null;
      end;
    end if;
  end if;
return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
