SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE abbott_execute_checkin ( nr_prescricao_p bigint, cd_barras_p text, nm_usuario_p text) AS $body$
DECLARE


cd_barras_w    numeric(38);
nm_usuario_w   usuario.nm_usuario%type;
ie_status_envio_w smallint;

BEGIN

select   coalesce(max(nm_usuario), nm_usuario_p)
into STRICT     nm_usuario_w
from     usuario
where    DS_LOGIN = nm_usuario_p;

select	max(a.ie_status_envio)
into STRICT	ie_status_envio_w	
from	lab_parametro a, prescr_medica b
WHERE	a.cd_estabelecimento = b.cd_estabelecimento and b.nr_prescricao = nr_prescricao_p;

CALL wheb_usuario_pck.set_nm_usuario(nm_usuario_w);

cd_barras_w    := obter_somente_numero(cd_barras_p);

update   prescr_proc_mat_item
set      ie_status = 25,
         nm_usuario = nm_usuario_w,
         dt_atualizacao = clock_timestamp()
where    nr_prescricao = nr_prescricao_p
and (ie_status <= 25 and ie_status >= ie_status_envio_w)
and      nr_seq_prescr_proc_mat in (SELECT nr_sequencia from prescr_proc_material x where x.nr_prescricao = nr_prescricao_p and cd_barras = cd_barras_w);

update   prescr_procedimento
set      ie_status_atend = 25,
         nm_usuario = nm_usuario_w,
         dt_atualizacao = clock_timestamp()
where    nr_prescricao = nr_prescricao_p
and (ie_status_atend <= 25 and ie_status_atend >= ie_status_envio_w)
and      nr_sequencia in (
            SELECT   y.nr_seq_prescr 
            from     prescr_proc_material x, 
                     prescr_proc_mat_item y,
                     prescr_procedimento z,
                     equipamento_lab x1,
         				lab_exame_equip x2
            where    x.nr_sequencia = y.nr_seq_prescr_proc_mat
            and      x.nr_prescricao = z.nr_prescricao
            and      y.nr_seq_prescr = z.nr_sequencia
            and      x2.nr_seq_exame = z.nr_seq_exame
            and      x2.cd_equipamento = x1.cd_equipamento
            and      x1.ds_sigla = 'ABBOTT'
            and      x.nr_prescricao = nr_prescricao_p
            and      x.cd_barras = cd_barras_w 
            and      z.nr_sequencia not in (SELECT o.nr_seq_prescr from prescr_proc_mat_item o where o.nr_prescricao = nr_prescricao_p and o.nr_seq_prescr = z.nr_sequencia and o.ie_status < 25));
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
