CREATE EXTENSION IF NOT EXISTS dblink;

CREATE OR REPLACE PROCEDURE abortar_se_dia_ciclo_ativo ( nr_seq_paciente_p bigint, cd_pessoa_fisica_p text) AS $body$
DECLARE
	-- Change this to reflect the dblink connection string
	v_conn_str  text := format('port=%s dbname=%s user=%s', current_setting('port'), current_database(), current_user);
	v_query     text;

BEGIN
	v_query := 'CALL abortar_se_dia_ciclo_ativo_atx ( ' || quote_nullable(nr_seq_paciente_p) || ',' || quote_nullable(cd_pessoa_fisica_p) || ' )';
	PERFORM * FROM dblink(v_conn_str, v_query) AS p (ret boolean);

END;
$body$ LANGUAGE plpgsql SECURITY DEFINER;




CREATE OR REPLACE PROCEDURE abortar_se_dia_ciclo_ativo_atx ( nr_seq_paciente_p bigint, cd_pessoa_fisica_p text) AS $body$
DECLARE


qt_reg_w	bigint := 0;
BEGIN

if (Obter_Funcao_Ativa	= 281) and (nr_seq_paciente_p IS NOT NULL AND nr_seq_paciente_p::text <> '') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	SELECT 	count(*)
	into STRICT 	qt_reg_w
	FROM	paciente_setor a,
			paciente_atendimento b
	WHERE 	a.nr_seq_paciente 	= nr_seq_paciente_p
	AND		coalesce(b.dt_real,clock_timestamp()) > clock_timestamp()
	AND		a.nr_seq_paciente = b.nr_seq_paciente
	AND		coalesce(b.dt_cancelamento::text, '') = ''
	AND		a.cd_pessoa_fisica	= cd_pessoa_fisica_p
	AND (coalesce(b.nr_prescricao::text, '') = '' or ((b.nr_prescricao IS NOT NULL AND b.nr_prescricao::text <> '') and obter_se_prescricao_liberada(b.nr_prescricao, 'M') = 'N'));

	if (qt_reg_w > 0) then

		CALL Wheb_mensagem_pck.exibir_mensagem_abort(269880);

	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
