SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE abrir_envio_lote ( nr_seq_lote_p text, nm_usuario_p text) AS $body$
DECLARE


ds_historico_w			varchar(255);
dt_remessa_retorno_w	timestamp;


BEGIN
if (nr_seq_lote_p IS NOT NULL AND nr_seq_lote_p::text <> '')then

	select 	dt_remessa_retorno
	into STRICT	dt_remessa_retorno_w
	from	lote_orgao_cobranca
	where nr_sequencia = nr_seq_lote_p;

	if (dt_remessa_retorno_w IS NOT NULL AND dt_remessa_retorno_w::text <> '') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(681905);
	end if;

	update lote_orgao_cobranca
	set dt_fechamento  = NULL
	where nr_sequencia = nr_seq_lote_p;

	ds_historico_w := wheb_mensagem_pck.get_texto(337825, 'NM_USUARIO=' || nm_usuario_p || ';DT_ATUALIZACAO=' || to_char(clock_timestamp(),'dd/MM/yyyy hh24:mi:ss'));
	CALL gerar_historico_cobr_orgao(null,null,nr_seq_lote_p,ds_historico_w,nm_usuario_p,'E');

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
