SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE acc_copiar_genero_alim (cd_estab_origem_p bigint, cd_estab_destino_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_genero_origem_w	nut_genero_alim.nr_sequencia%type;
nr_seq_genero_novo_w	nut_genero_alim.nr_sequencia%type;
ds_genero_w		nut_genero_alim.ds_genero%type;
cd_material_w		nut_genero_alim.cd_material%type;
qt_genero_w		bigint;
				
C01 CURSOR FOR
	SELECT	nr_sequencia
	from	nut_genero_alim
	where	cd_estabelecimento = cd_estab_origem_p
	order by 1;

       

BEGIN

open C01;
loop
fetch C01 into	
	nr_seq_genero_origem_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	
	select	max(ds_genero),
		max(cd_material)
	into STRICT	ds_genero_w,
		cd_material_w
	from	nut_genero_alim
	where	nr_sequencia = nr_seq_genero_origem_w;
	
	select	count(*)
	into STRICT	qt_genero_w
	from	nut_genero_alim
	where	ds_genero = ds_genero_w
	and	coalesce(cd_material,0) = coalesce(cd_material_w,0)
	and	cd_estabelecimento = cd_estab_destino_p;
	
	if (qt_genero_w = 0) then
		select	nextval('nut_genero_alim_seq')
		into STRICT	nr_seq_genero_novo_w
		;
		
		insert into nut_genero_alim(	
			nr_sequencia,
			ds_genero,
			dt_atualizacao,
			nm_usuario,
			cd_material,
			ie_situacao,
			qt_conversao,
			cd_estabelecimento,
			nr_seq_tabela,
			qt_fator_correcao,
			qt_fator_coccao,
			ie_arred_req,
			ie_mostra_item_extra,
			nr_seq_tipo_alimento,
			ie_gerar_solic_compra,
			cd_unidade_medida)
		(SELECT	nr_seq_genero_novo_w,
			ds_genero,
			clock_timestamp(),
			nm_usuario_p,
			cd_material,
			ie_situacao,
			qt_conversao,
			cd_estab_destino_p,
			nr_seq_tabela,
			qt_fator_correcao,
			qt_fator_coccao,
			ie_arred_req,
			ie_mostra_item_extra,
			nr_seq_tipo_alimento,
			ie_gerar_solic_compra,
			cd_unidade_medida
		from	nut_genero_alim
		where	nr_sequencia = nr_seq_genero_origem_w);
		
		insert into NUT_COMP_NUTRICIONAL(	
			nr_sequencia,
			nr_seq_nutriente,
			nr_seq_gen_alim,
			dt_atualizacao,
			nm_usuario,
			qt_composicao,
			ie_informacao,
			nr_seq_apresent)
		(SELECT	nextval('nut_comp_nutricional_seq'),
			nr_seq_nutriente,
			nr_seq_genero_novo_w,
			clock_timestamp(),
			nm_usuario_p,
			qt_composicao,
			ie_informacao,
			nr_seq_apresent
		from	nut_comp_nutricional
		where	nr_seq_gen_alim = nr_seq_genero_origem_w);
		
		insert into NUT_GRUPO_GENERO(	
			nr_sequencia,
			nr_seq_genero,
			nr_seq_grupo,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec)
		(SELECT	nextval('nut_grupo_genero_seq'),
			nr_seq_genero_novo_w,
			nr_seq_grupo,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p
		from	nut_grupo_genero
		where	nr_seq_genero = nr_seq_genero_origem_w);	
	end if;
	
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE acc_copiar_genero_alim (cd_estab_origem_p bigint, cd_estab_destino_p bigint, nm_usuario_p text) FROM PUBLIC;

