CREATE OR REPLACE PROCEDURE aceitar_divergencia_inspecao ( nr_sequencia_p bigint, cd_pessoa_resp_p text, ds_justificativa_p text, nm_usuario_p text) AS $body$
BEGIN

update	inspecao_divergencia
set	cd_pessoa_resp_justif = cd_pessoa_resp_p,
	ie_motivo_aceite = 'A',
	ds_justificativa = substr(ds_justificativa_p,1,255),
	nm_usuario = nm_usuario_p,
	dt_atualizacao = clock_timestamp()
where	nr_sequencia = nr_sequencia_p;


commit;

end;
