CREATE OR REPLACE PROCEDURE acertar_diferenca_rateio_oc ( nr_ordem_compra_p bigint, nr_item_oci_p bigint) AS $body$
DECLARE


vl_total_rateio_w	double precision;
cd_centro_custo_w	integer;
nr_item_oci_w		bigint;
vl_total_item_ordem_w	double precision;
nr_sequencia_w		bigint;

c00 CURSOR FOR
SELECT	nr_item_oci,
	(vl_unitario_material * qt_material)
from	ordem_compra_item
where	nr_ordem_compra		= nr_ordem_compra_p
and (nr_item_oci = nr_item_oci_p or nr_item_oci_p = 0);


BEGIN

open c00;
loop
fetch c00 into
	nr_item_oci_w,
	vl_total_item_ordem_w;
EXIT WHEN NOT FOUND; /* apply on c00 */
	begin

	select	max(nr_sequencia)
	into STRICT	nr_sequencia_w
	from	ordem_compra_item_rateio
	where	nr_ordem_compra		= nr_ordem_compra_p;

	select	sum(vl_rateio)
	into STRICT	vl_total_rateio_w
	from	ordem_compra_item_rateio
	where	nr_ordem_compra		= nr_ordem_compra_p
	and	nr_item_oci 		= nr_item_oci_w;

	if (vl_total_rateio_w <> vl_total_item_ordem_w) then
		update	ordem_compra_item_rateio
		set	vl_rateio	= (vl_rateio + (vl_total_item_ordem_w - vl_total_rateio_w))
		where	nr_sequencia	= nr_sequencia_w;
	end if;

	end;
end loop;
close c00;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
