CREATE OR REPLACE PROCEDURE acertar_duplic_comprador ( cd_pessoa_origem_p text, cd_pessoa_destino_p text) AS $body$
DECLARE


qt_comprador_w	bigint;


BEGIN

if (cd_pessoa_origem_p IS NOT NULL AND cd_pessoa_origem_p::text <> '') and (cd_pessoa_destino_p IS NOT NULL AND cd_pessoa_destino_p::text <> '') then

	select	count(*)
	into STRICT	qt_comprador_w
	from	comprador
	where	cd_pessoa_fisica = cd_pessoa_destino_p;

	if (qt_comprador_w = 0)  then
		insert into comprador(
			cd_pessoa_fisica,
			nm_guerra,
			dt_atualizacao,
			nm_usuario,
			nr_telefone,
			ds_email,
			cd_estabelecimento,
			ie_situacao,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_fax)
		SELECT	cd_pessoa_destino_p,
			nm_guerra,
			dt_atualizacao,
			nm_usuario,
			nr_telefone,
			ds_email,
			cd_estabelecimento,
			ie_situacao,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_fax
		from	comprador
		where	cd_pessoa_fisica = cd_pessoa_origem_p;

	end if;

	update	ordem_compra
	set	cd_comprador = cd_pessoa_destino_p
	where	cd_comprador = cd_pessoa_origem_p;

	update	cot_compra
	set	cd_comprador = cd_pessoa_destino_p
	where	cd_comprador = cd_pessoa_origem_p;

	update	local_estoque
	set	cd_comprador_consig = cd_pessoa_destino_p
	where	cd_comprador_consig = cd_pessoa_origem_p;

	update	autorizacao_cirurgia
	set	cd_comprador = cd_pessoa_destino_p
	where	cd_comprador = cd_pessoa_origem_p;

	update	parametro_compras
	set	cd_comprador_padrao = cd_pessoa_destino_p
	where	cd_comprador_padrao = cd_pessoa_origem_p;

	update	parametro_compras
	set	cd_comprador_consig = cd_pessoa_destino_p
	where	cd_comprador_consig = cd_pessoa_origem_p;

	delete
	from	comprador
	where	cd_pessoa_fisica = cd_pessoa_origem_p;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
