CREATE OR REPLACE PROCEDURE acertar_duplic_med_plantao (cd_pessoa_origem_p text, cd_pessoa_destino_p text) AS $body$
BEGIN
if (cd_pessoa_origem_p IS NOT NULL AND cd_pessoa_origem_p::text <> '') and (cd_pessoa_destino_p IS NOT NULL AND cd_pessoa_destino_p::text <> '') then

	insert into medico_plantao(
					nr_sequencia,
					dt_atualizacao,
					nm_usuario,
					cd_medico,
					dt_inicial,
					dt_final,
					qt_minuto,
					cd_estabelecimento,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					dt_chamado,
					cd_especialidade,
					nr_atendimento
					)
	SELECT				nextval('medico_plantao_seq'),
					a.dt_atualizacao,
					a.nm_usuario,
					cd_pessoa_destino_p,
					a.dt_inicial,
					a.dt_final,
					a.qt_minuto,
					a.cd_estabelecimento,
					a.dt_atualizacao_nrec,
					a.nm_usuario_nrec,
					a.dt_chamado,
					a.cd_especialidade,
					a.nr_atendimento
	from				medico_plantao a
	where				a.cd_medico = cd_pessoa_origem_p
	and				not exists (	SELECT	1
							from	medico_plantao x
							where	x.cd_medico		= cd_pessoa_destino_p
							and	x.cd_especialidade	= a.cd_especialidade);

	delete
	from	medico_plantao
	where	cd_medico = cd_pessoa_origem_p;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
