CREATE OR REPLACE PROCEDURE acertar_duplic_med_terceiro ( cd_pessoa_origem_p text, cd_pessoa_destino_p text) AS $body$
DECLARE


nr_seq_terceiro_w	bigint;
qt_destino_w		bigint;

c01 CURSOR FOR
	SELECT	nr_seq_terceiro
	from 	terceiro_pessoa_fisica
	where 	cd_pessoa_fisica 	= cd_pessoa_origem_p;


BEGIN

open c01;
loop
fetch c01 into
	nr_seq_terceiro_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	begin
	select	count(*)
	into STRICT	qt_destino_w
	from	terceiro_pessoa_fisica
	where	nr_seq_terceiro		= nr_seq_terceiro_w
	and	cd_pessoa_fisica	= cd_pessoa_destino_p;
	exception
	when others then
		qt_destino_w	:= 0;
	end;

	/* Caso já tenha, exclui, caso não tenha atualiza */

	if (qt_destino_w > 0) then
		delete	from terceiro_pessoa_fisica
		where	cd_pessoa_fisica 	= cd_pessoa_origem_p
		and 	nr_seq_terceiro		= nr_seq_terceiro_w;
	else
		update	terceiro_pessoa_fisica
		set 	cd_pessoa_fisica 	= cd_pessoa_destino_p
		where	cd_pessoa_fisica 	= cd_pessoa_origem_p
		and	nr_seq_terceiro 	= nr_seq_terceiro_w;
	end if;

	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
