		NR_SEQ_CERTIF,
		cd_pessoa_destino_p,
		DT_TITULACAO,
		CD_ESPEC_TITULACAO,
		clock_timestamp(),
		clock_timestamp(),
		nm_usuario,
		nm_usuario_nrec
from 	MEDICO_TITULACAO a
where	a.cd_pessoa_fisica = cd_pessoa_origem_p
and		not exists (	select	1
					from 	MEDICO_TITULACAO x
