					and		x.NR_SEQ_TITULACAO = a.NR_SEQ_TITULACAO
					and		x.IE_TIPO_CERTIF = a.IE_TIPO_CERTIF);

delete	FROM MEDICO_TITULACAO
where	cd_pessoa_fisica = cd_pessoa_origem_p;


