SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE acertar_duplic_pessoa_classif ( cd_pessoa_origem_p text, cd_pessoa_destino_p text) AS $body$
DECLARE


nr_sequencia_w	bigint;
qt_destino_w	bigint;

c01 CURSOR FOR
	SELECT	nr_sequencia
	from 	pessoa_classif
	where 	cd_pessoa_fisica 	= cd_pessoa_origem_p;


BEGIN

open c01;
loop
fetch c01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	begin
	select	count(*)
	into STRICT	qt_destino_w
	from	pessoa_classif
	where	cd_pessoa_fisica	= cd_pessoa_destino_p;
	exception
	when others then
		qt_destino_w	:= 0;
	end;
	
	/* Caso já tenha, exclui, caso não tenha atualiza */

	if (qt_destino_w > 0) then
		delete	from pessoa_classif_espec a
		where	exists (SELECT	1
				from	pessoa_classif b
				where	a.nr_seq_classif_pf = b.nr_sequencia
				and	b.cd_pessoa_fisica = cd_pessoa_origem_p);
						
		delete	from pessoa_classif
		where	cd_pessoa_fisica 	= cd_pessoa_origem_p;
	else
		update	pessoa_classif
		set 	cd_pessoa_fisica 	= cd_pessoa_destino_p
		where	cd_pessoa_fisica 	= cd_pessoa_origem_p 
		and	nr_sequencia 		= nr_sequencia_w;
	end if;	
	
	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
