SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE acertar_duplic_prontuario ( cd_pessoa_origem_p text, cd_pessoa_destino_p text) AS $body$
DECLARE


nr_pront_dest_w      bigint;
nr_seq_orig_w        bigint;

c01 CURSOR FOR
SELECT  nr_sequencia
FROM    same_cpi_prontuario
where   cd_pessoa_fisica        = cd_pessoa_origem_p;


BEGIN

      SELECT       coalesce(MAX(nr_prontuario),0)
      INTO STRICT nr_pront_dest_w
      FROM pessoa_fisica
      WHERE        cd_pessoa_fisica        = cd_pessoa_destino_p;

      OPEN c01;
            LOOP
            FETCH        c01 INTO nr_seq_orig_w;
            BEGIN
                  EXIT WHEN NOT FOUND; /* apply on c01 */

                  if (nr_pront_dest_w > 0) THEN
                        delete  from same_cpi_prontuario
                        where   nr_sequencia = nr_seq_orig_w;

                  else    /* Rafael em 26/04/2007 OS55775 */
                        update  same_cpi_prontuario
                        set     cd_pessoa_fisica = cd_pessoa_destino_p
                        where   nr_sequencia = nr_seq_orig_w;
                  end     if;
            END;
            END LOOP;
      CLOSE c01;

 commit;

 end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE acertar_duplic_prontuario ( cd_pessoa_origem_p text, cd_pessoa_destino_p text) FROM PUBLIC;

