CREATE OR REPLACE PROCEDURE acertar_duplic_pront_estab ( cd_pessoa_origem_p text, cd_pessoa_destino_p text) AS $body$
DECLARE


qt_registro_w			bigint	:= 0;

c01 CURSOR FOR
SELECT	a.cd_estabelecimento,
	a.nr_prontuario,
	a.nm_usuario
from	pessoa_fisica_pront_estab a
where	a.cd_pessoa_fisica	= cd_pessoa_origem_p;

vet01	c01%RowType;


BEGIN

open C01;
loop
fetch C01 into
	vet01;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	count(*)
	into STRICT	qt_registro_w
	from	pessoa_fisica_pront_estab a
	where	a.cd_pessoa_fisica		= cd_pessoa_destino_p
	and	a.cd_estabelecimento	= vet01.cd_estabelecimento;

	if (qt_registro_w = 0) then

		insert into pessoa_fisica_pront_estab(
			nr_sequencia,
			nm_usuario,
			dt_atualizacao,
			nm_usuario_nrec,
			dt_atualizacao_nrec,
			cd_pessoa_fisica,
			cd_estabelecimento,
			nr_prontuario)
		values (	nextval('pessoa_fisica_pront_estab_seq'),
			vet01.nm_usuario,
			clock_timestamp(),
			vet01.nm_usuario,
			clock_timestamp(),
			cd_pessoa_destino_p,
			vet01.cd_estabelecimento,
			vet01.nr_prontuario);

	end if;

	delete	FROM pessoa_fisica_pront_estab
	where	cd_pessoa_fisica	= cd_pessoa_origem_p
	and	cd_estabelecimento	= vet01.cd_estabelecimento;

	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
