CREATE OR REPLACE PROCEDURE acertar_duplic_same_prontuario ( cd_pessoa_origem_p text, nr_pront_origem_p bigint, cd_pessoa_destino_p text) AS $body$
DECLARE


nr_prontuario_w	bigint;
nr_seq_orig_w	bigint;

c01 CURSOR FOR
	SELECT	nr_sequencia
	FROM	same_prontuario
	where	cd_pessoa_fisica	= cd_pessoa_origem_p;


BEGIN

OPEN	c01;
LOOP
FETCH	c01 INTO nr_seq_orig_w;
	BEGIN
	EXIT WHEN NOT FOUND; /* apply on c01 */

		update	same_prontuario
		set	cd_pessoa_fisica = cd_pessoa_destino_p,
			NR_PRONTUARIO_ANT = nr_pront_origem_p
		where	nr_sequencia = nr_seq_orig_w;

	END;
	END LOOP;
CLOSE c01;

commit;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
