CREATE OR REPLACE PROCEDURE acerta_diverg_convenio_conta ( nr_interno_conta_p bigint) AS $body$
DECLARE


cd_convenio_w			integer	:= 0;
cd_categoria_w			varchar(10)	:= '0';
cd_estabelecimento_w		smallint;
ie_convenio_conta_param_w	varchar(1);


BEGIN

select	coalesce(max(cd_categoria_parametro),'0'),
	coalesce(max(cd_convenio_parametro),0),
	coalesce(max(cd_estabelecimento),0)
into STRICT	cd_categoria_w,
	cd_convenio_w,
	cd_estabelecimento_w
from	conta_paciente
where	nr_interno_conta	= nr_interno_conta_p;

if (coalesce(cd_estabelecimento_w,0) > 0) then

	select	coalesce(max(ie_convenio_conta_param),'N')
	into STRICT	ie_convenio_conta_param_w
	from	parametro_faturamento
	where	cd_estabelecimento = cd_estabelecimento_w;

	if (ie_convenio_conta_param_w = 'S') then
		select	coalesce(max(coalesce(cd_categoria_calculo, cd_categoria_parametro)),'0'),
			coalesce(max(coalesce(cd_convenio_calculo, cd_convenio_parametro)),0)
		into STRICT	cd_categoria_w,
			cd_convenio_w
		from	conta_paciente
		where	nr_interno_conta	= nr_interno_conta_p;
	end if;
end if;

if (cd_convenio_w > 0) and (cd_categoria_w <> '0') then
	update	procedimento_paciente
	set	cd_convenio		= cd_convenio_w,
		cd_categoria		= cd_categoria_w
	where	nr_interno_conta	= nr_interno_conta_p
	and	cd_convenio		<> cd_convenio_w;

	update	material_atend_paciente
	set	cd_convenio		= cd_convenio_w,
		cd_categoria		= cd_categoria_w
	where	nr_interno_conta	= nr_interno_conta_p
	and	cd_convenio		<> cd_convenio_w;
end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
