CREATE OR REPLACE PROCEDURE ac_sel_convenios_regra_turno ( nr_seq_turno_p bigint, qt_permissao_p bigint, ie_atende_convenio_p text, cd_agenda_p bigint, hr_inicial_p timestamp, ie_dia_semana_p bigint, ds_lista_cd_conv_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


cd_convenio_w		bigint;
tamanho_lista_w		bigint;
posicao_virgula_w 	smallint;
nr_max_loop_w		smallint := 9999;
ds_lista_w 		varchar(10000);


BEGIN
if (ds_lista_cd_conv_p IS NOT NULL AND ds_lista_cd_conv_p::text <> '') then
	begin
	tamanho_lista_w := length(ds_lista_cd_conv_p);
	ds_lista_w := ds_lista_cd_conv_p;

	while(ds_lista_w IS NOT NULL AND ds_lista_w::text <> '') and (nr_max_loop_w > 0) loop
		begin
		posicao_virgula_w := position(',' in ds_lista_w);
		if (posicao_virgula_w > 1) and ((substr(ds_lista_w, 1, posicao_virgula_w -1) IS NOT NULL AND (substr(ds_lista_w, 1, posicao_virgula_w -1))::text <> '')) then
			begin
			cd_convenio_w := (substr(ds_lista_w, 1, posicao_virgula_w -1))::numeric;
			ds_lista_w := substr(ds_lista_w, posicao_virgula_w +1, tamanho_lista_w);
			end;
		elsif (ds_lista_w IS NOT NULL AND ds_lista_w::text <> '') then
			begin
			cd_convenio_w := replace(ds_lista_w, ',', '');
			ds_lista_w := '';
			end;
		end if;

		if (cd_convenio_w IS NOT NULL AND cd_convenio_w::text <> '') and (cd_convenio_w > 0) then
			begin

			CALL Agcons_insere_regr_turn_conv(nr_seq_turno_p,
						qt_permissao_p,
						ie_atende_convenio_p,
						cd_convenio_w,
						cd_agenda_p,
						nm_usuario_p,
						hr_inicial_p,
						ie_dia_semana_p);


			end;
		end if;

		nr_max_loop_w := nr_max_loop_w -1;
		end;
	end loop;
	end;
end if;

commit;
end;
$body$
