        dt_atualizacao,
        nm_usuario
      ) values (
        nr_sequencia_w,
        cd_pessoa_fisica_p,
        cd_tipo_pessoa_p,
        clock_timestamp(),
        nm_usuario_p
      );

  when 'DELETE' then
    delete from pessoa_fisica_aux
    where cd_pessoa_fisica    = cd_pessoa_fisica_p;
	
  when 'UPDATE' then
	update pessoa_fisica_aux
	set cd_tipo_pessoa 	   = cd_tipo_pessoa_p,
		dt_atualizacao       = clock_timestamp(),
		nm_usuario           = nm_usuario_p
	where cd_pessoa_fisica = cd_pessoa_fisica_p;	
	
  end case;

  commit;
end;
