SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE adcionar_item_manip_quimio ( nr_seq_ordem_p bigint, cd_material_p bigint, qt_material_p bigint, nr_seq_lote_fornec_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
qt_preparo_quimio_w	bigint;
cd_unidade_medida_w	varchar(30);
ie_existe_w		varchar(1);

BEGIN


select 	count(*)
into STRICT	qt_preparo_quimio_w
from 	w_preparo_quimio_lote
where 	cd_material = cd_material_p
and 	nr_seq_ordem = nr_seq_ordem_p
and 	nr_seq_lote_fornec = nr_seq_lote_fornec_p;

select 	max(cd_unidade_medida_solic)
into STRICT	cd_unidade_medida_w
from 	material
where 	cd_material = cd_material_p;

if (substr(obter_tipo_material(cd_material_p,'C'),1,1) = '1')  then
	select	coalesce(max('S'),'N')
	into STRICT 	ie_existe_w
	from 	prescr_material a
	where	a.nr_prescricao = (SELECT 	max(b.nr_prescricao)
				   from		can_ordem_item_prescr d,
						can_ordem_prod b
				   where 	d.nr_seq_ordem = b.nr_sequencia
				   and		b.nr_sequencia = nr_seq_ordem_p
				   and		a.nr_seq_kit = d.nr_seq_prescricao)
	and a.cd_material = cd_material_p;

	if (ie_existe_w = 'N') then
		select	coalesce(max('S'),'N')
		into STRICT 	ie_existe_w
		from 	prescr_material a
		where	a.nr_prescricao_original = (SELECT 	max(b.nr_prescricao)
						   from		can_ordem_item_prescr d,
								can_ordem_prod b
						   where 	d.nr_seq_ordem = b.nr_sequencia
						   and		b.nr_sequencia = nr_seq_ordem_p
						   and		a.nr_seq_mat_disp = d.nr_seq_prescricao)
		and a.cd_material = cd_material_p;
	end if;

	if (ie_existe_w = 'N') then
		CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(194714);
	end if;
end if;

if (qt_preparo_quimio_w < 1) and (cd_material_p IS NOT NULL AND cd_material_p::text <> '') and (qt_material_p IS NOT NULL AND qt_material_p::text <> '') then
	select	nextval('w_preparo_quimio_lote_seq')
	into STRICT	nr_sequencia_w
	;

	insert into w_preparo_quimio_lote(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_ordem,
			cd_material,
			qt_material,
			cd_unidade_medida,
			nr_seq_lote_fornec)
	values (nr_sequencia_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_ordem_p,
			cd_material_p,
			qt_material_p,
			cd_unidade_medida_w,
			nr_seq_lote_fornec_p);

	commit;
elsif (qt_preparo_quimio_w > 0) and (cd_material_p IS NOT NULL AND cd_material_p::text <> '') and (qt_material_p IS NOT NULL AND qt_material_p::text <> '') then

	update	w_preparo_quimio_lote
	set 	qt_material = qt_material + qt_material_p
	where 	cd_material = cd_material_p
	and 	nr_seq_ordem = nr_seq_ordem_p
	and 	nr_seq_lote_fornec = nr_seq_lote_fornec_p;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE adcionar_item_manip_quimio ( nr_seq_ordem_p bigint, cd_material_p bigint, qt_material_p bigint, nr_seq_lote_fornec_p bigint, nm_usuario_p text) FROM PUBLIC;

