SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE adc_fechamento_rec_prot_doc ( nr_sequencia_p bigint, nm_usuario_p text, ie_status_protocolo_p text) AS $body$
BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '')
	and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	update	protocolo_documento
	set    	dt_fechamento		= clock_timestamp(),
		dt_rec_destino		= clock_timestamp(),
		nm_usuario_receb  	= nm_usuario_p,
		ie_status_protocolo	= ie_status_protocolo_p
	where  	nr_sequencia      	= nr_sequencia_p;

	update	protocolo_doc_item
	set	dt_recebimento   	= clock_timestamp(),
		nm_usuario_receb	= nm_usuario_p
	where	coalesce(dt_recebimento::text, '') = ''
	and	nr_sequencia    	= nr_sequencia_p
	and	coalesce(dt_retirada::text, '') = '';
	end;
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
