CREATE OR REPLACE PROCEDURE add_build_on_queue ( cd_versao_p text, ie_aplicacao_p text, nm_usuario_solicitante_p text, nr_seq_ordem_serv_p bigint default null, ie_custom_branch_p text default 'N') AS $body$
DECLARE

cd_versao_w               varchar(20);
ie_aplicacao_w            varchar(1);
nr_seq_man_os_ctrl_desc_w bigint := null;
nr_seq_man_os_build_emer_w bigint := null;
nr_seq_man_versao_calendario_w bigint := null;
nm_usuario_solicitante_w  varchar(15);
nr_seq_ordem_serv_w		  bigint;
dt_atualizacao_w timestamp;
branch_name_w man_custom_branch.branch_name%TYPE := null;


BEGIN
	if (not coalesce(nr_seq_ordem_serv_p::text, '') = '') then
		select coalesce(min(nr_sequencia),0) into STRICT nr_seq_man_os_build_emer_w
		  from man_os_ctrl_build_emer
		 where nr_seq_ordem_serv = nr_seq_ordem_serv_p
		   and cd_versao = cd_versao_p
		   and ie_aplicacao = ie_aplicacao_p
		   and ie_status_geracao <> 'G'
		 order by nr_sequencia;
		
		if ( coalesce(nr_seq_man_os_build_emer_w::text, '') = '' or nr_seq_man_os_build_emer_w <= 0) then
			RAISE EXCEPTION '%', 'Build request not found for SO or invalid SO number! [nr_seq_man_os_build_emer_w='||nr_seq_man_os_build_emer_w||'][nr_seq_ordem_serv_p='||nr_seq_ordem_serv_p||']' USING ERRCODE = '45000';
		end if;
		nr_seq_ordem_serv_w := nr_seq_ordem_serv_p;
		if (length(ie_custom_branch_p) > 0 and ie_custom_branch_p != 'N') then
			select customer_branch_exists(nr_seq_ordem_serv_p)  into STRICT branch_name_w;
			if (coalesce(branch_name_w::text, '') = '' or length(branch_name_w) <= 0) then
				branch_name_w := cd_versao_w;
			end if;
		end if;
	end if;

	cd_versao_w := cd_versao_p;

	if (coalesce(branch_name_w::text, '') = '' or length(branch_name_w) <= 0) then
		branch_name_w := cd_versao_w;
	end if;

	if IE_APLICACAO_P = 'H' then
		ie_aplicacao_w := 'F';
	else
		ie_aplicacao_w := ie_aplicacao_p;
	end if;

	nm_usuario_solicitante_w := nm_usuario_solicitante_p;

	if (coalesce(dt_atualizacao_w::text, '') = '') then
		dt_atualizacao_w := clock_timestamp();
	end if;

	EXECUTE 'insert into tasy_queue_build@orcl (
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		cd_versao,
		ie_aplicacao,
		nr_seq_man_os_ctrl_desc,
		nm_usuario_solicitante,
		ie_status_build,
		NR_SEQ_MAN_OS_BUILD_EMER,
		branch_name)
	values (
		tasy_queue_build_seq.nextval@orcl,
		:dt_atualizacao_w,
		''Tasy'',
		sysdate,
		''Tasy'',
		:cd_versao_w,
		:ie_aplicacao_w,
		:nr_seq_man_os_ctrl_desc_w,
		:nm_usuario_solicitante_w,
		''A'',
		:nr_seq_man_os_build_emer_w,
		:branch_name_w)'
    using dt_atualizacao_w, cd_versao_w, ie_aplicacao_w, nr_seq_man_os_ctrl_desc_w, nm_usuario_solicitante_w, nr_seq_man_os_build_emer_w, branch_name_w;

end;
$body$
