CREATE OR REPLACE PROCEDURE add_error_status_import_arq (nr_seq_status_imp_p sus_status_arq_txt.nr_seq_status_imp%type, nm_arquivo_p sus_status_arq_txt.nm_arquivo%type, nm_usuario_p sus_status_arq_txt.nm_usuario%type, cd_exp_status_imp_p sus_status_arq_txt.cd_exp_status_imp%type) AS $body$
DECLARE


qtd_status_import_arq sus_status_arq_txt.nr_sequencia%type;


BEGIN

select  count(1)
into STRICT    qtd_status_import_arq
from    sus_status_arq_txt a
where   a.nr_seq_status_imp = nr_seq_status_imp_p
and     a.nm_arquivo = nm_arquivo_p
and     a.nm_usuario = nm_usuario_p;

if (qtd_status_import_arq <> 0)then

update  sus_status_arq_txt a
set     a.ie_status_import = 'E',
        a.cd_exp_status_imp = cd_exp_status_imp_p,
        a.dt_atualizacao = clock_timestamp(),
        a.nm_usuario = nm_usuario_p
where   a.nm_arquivo = nm_arquivo_p
and     a.nr_seq_status_imp = nr_seq_status_imp_p;

commit;

else

CALL insert_sus_status_arq_txt(nm_usuario_p, nr_seq_status_imp_p, cd_exp_status_imp_p, nm_arquivo_p, 'E');

end if;

end;
$body$
