CREATE OR REPLACE PROCEDURE add_phrase_builder_fields ( nm_tabela_p phrase_builder_fields.nm_tabela%type, nm_atribute_p phrase_builder_fields.ds_field_name%type, nr_seq_grupo_p phrase_builder_fields.nr_seq_grupo%type ) AS $body$
DECLARE


quantidade_registros_w smallint;


BEGIN
  select
	coalesce(max(1),0)
  into STRICT 
	quantidade_registros_w
  from phrase_builder_fields
  where nm_tabela             = nm_tabela_p
  and ds_field_name           = nm_atribute_p  LIMIT 1;

  if ( quantidade_registros_w > 0 ) then
    update phrase_builder_fields
    set nr_seq_grupo      = nr_seq_grupo_p,
      dt_atualizacao_nrec = clock_timestamp(),
      nm_usuario_nrec     = wheb_usuario_pck.get_nm_usuario()
    where nm_tabela       = nm_tabela_p
    and ds_field_name     = nm_atribute_p;

  else
    insert into phrase_builder_fields(
        nr_seq_grupo,
        ds_field_name,
        dt_atualizacao,
        nm_usuario,
        dt_atualizacao_nrec,
        nm_usuario_nrec,
        nm_tabela
      )
      values (
        nr_seq_grupo_p,
        nm_atribute_p,
        clock_timestamp(),
        wheb_usuario_pck.get_nm_usuario(),
        clock_timestamp(),
        wheb_usuario_pck.get_nm_usuario(),
        nm_tabela_p
      );
  end if;
end;
$body$
