CREATE OR REPLACE PROCEDURE adep_associar_lote_fornec (nr_lote_fornec_p bigint, nr_seq_horario_p bigint, cd_material_p bigint, ie_existe_lote_p INOUT text) AS $body$
DECLARE


ie_existe_lote_w	varchar(1) := 'N';
nr_seq_material_w prescr_mat_hor.nr_seq_material%TYPE;
nr_prescricao_w prescr_mat_hor.nr_prescricao%TYPE;


BEGIN

begin
	select	'S'
	into STRICT	ie_existe_lote_w
	from	material_lote_fornec a
	where	a.nr_sequencia = nr_lote_fornec_p
	and 	a.cd_material = cd_material_p;
exception
	when no_data_found then
		ie_existe_lote_w := 'N';
end;

select	nr_seq_material, nr_prescricao
into STRICT	nr_seq_material_w, nr_prescricao_w
from	prescr_mat_hor
where	nr_sequencia = nr_seq_horario_p;

if (ie_existe_lote_w = 'S') then

	update	prescr_mat_hor
	set		nr_seq_lote_fornec = nr_lote_fornec_p
	where	nr_sequencia = nr_seq_horario_p;

  if (nr_seq_material_w IS NOT NULL AND nr_seq_material_w::text <> '') and (nr_prescricao_w IS NOT NULL AND nr_prescricao_w::text <> '') then
	  update	prescr_material
	  set		nr_seq_lote_fornec = nr_lote_fornec_p
	  where	nr_prescricao = nr_prescricao_w
	  and		nr_sequencia = nr_seq_material_w;
  end if;

	commit;
end if;

ie_existe_lote_p := ie_existe_lote_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
