SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE adep_checagem_automatica_proc ( nr_prescricao_p bigint, nm_usuario_p text, cd_perfil_p bigint) AS $body$
DECLARE




cd_procedimento_w				bigint;

cd_material_w				bigint;

nr_sequencia_proc_w				bigint;

nr_sequencia_mat_w				bigint;

nr_seq_horario_w			bigint;

nr_atendimento_w			bigint;

cd_setor_Atendimento_w		bigint;

dt_horario_w				timestamp;

ds_texto_aux_w				varchar(255);



c01 CURSOR FOR

SELECT	a.cd_procedimento,

		a.nr_sequencia

from  prescr_procedimento a

inner join  cpoe_procedimento b on b.nr_sequencia = a.nr_seq_proc_cpoe

where	a.nr_prescricao	= nr_prescricao_p

and		b.ie_record_execution	= 'S';



c02 CURSOR FOR

SELECT	pp.nr_sequencia,

    pp.dt_horario

from (
  SELECT  nr_sequencia,
    dt_horario,
    ROW_NUMBER() OVER (order by cast(dt_horario as timestamp)) seqnum
  from  prescr_proc_hor
  where nr_prescricao	= nr_prescricao_p
  and		nr_seq_procedimento	= nr_sequencia_proc_w
  and		cd_procedimento	= cd_procedimento_w
) pp

where	seqnum	= 1;



c03 CURSOR FOR

SELECT	a.cd_material,

		a.nr_sequencia

from  prescr_material a

inner join prescr_procedimento b on (b.nr_prescricao = a.nr_prescricao)

and (a.nr_sequencia_proc = b.nr_sequencia)

inner join  cpoe_procedimento c on c.nr_sequencia = b.nr_seq_proc_cpoe

where	a.nr_prescricao	= nr_prescricao_p

and		c.ie_record_execution	= 'S';




c04 CURSOR FOR

SELECT	pp.nr_sequencia,

    pp.dt_horario

from (
  SELECT  nr_sequencia,
    dt_horario, 
    ROW_NUMBER() OVER (order by cast(dt_horario as timestamp)) seqnum
  from  prescr_mat_hor
  where nr_prescricao	= nr_prescricao_p
  and		nr_seq_material	= nr_sequencia_mat_w
  and		cd_material	= cd_material_w
) pp

where	seqnum	= 1;





BEGIN



select	max(nr_atendimento),

		max(cd_setor_Atendimento)

into STRICT	nr_atendimento_w,

		cd_setor_Atendimento_w

from	prescr_medica

where	nr_prescricao	= nr_prescricao_p;



ds_texto_aux_w	:= wheb_mensagem_pck.get_texto(300552);



open C01;

loop

fetch C01 into	

		cd_procedimento_w,

		nr_sequencia_proc_w;

EXIT WHEN NOT FOUND; /* apply on C01 */

	begin



  open C02;

  loop

  fetch C02 into	

    nr_seq_horario_w,

    dt_horario_w;

  EXIT WHEN NOT FOUND; /* apply on C02 */

    begin



    update	prescr_proc_hor

    set	dt_fim_horario 	= clock_timestamp(),

      nm_usuario	= nm_usuario_p,

      nm_usuario_adm	= nm_usuario_p,			

      dt_atualizacao	= clock_timestamp()

    where	nr_sequencia	= nr_seq_horario_w;



    CALL gerar_alter_hor_prescr_adep(	nr_atendimento_w, 'P', cd_procedimento_w, nr_prescricao_p, nr_sequencia_proc_w, 
    
            nr_seq_horario_w, dt_horario_w, 3, null, ds_texto_aux_w, null, nm_usuario_p);

    end;

  end loop;

  close C02;

	end;

end loop;

close C01;


open C03;

loop

fetch C03 into	

		cd_material_w,

		nr_sequencia_mat_w;

EXIT WHEN NOT FOUND; /* apply on C03 */

	begin



  open C04;

  loop

  fetch C04 into	

    nr_seq_horario_w,

    dt_horario_w;

  EXIT WHEN NOT FOUND; /* apply on C04 */

    begin


    
    update	prescr_mat_hor

    set	dt_fim_horario 	= clock_timestamp(),

      nm_usuario	= nm_usuario_p,

      nm_usuario_adm	= nm_usuario_p,			

      dt_atualizacao	= clock_timestamp()

    where	nr_sequencia	= nr_seq_horario_w;



    CALL gerar_alter_hor_prescr_adep(	nr_atendimento_w, 'M', cd_material_w, nr_prescricao_p, nr_sequencia_mat_w, 
    
            nr_seq_horario_w, dt_horario_w, 3, null, ds_texto_aux_w, null, nm_usuario_p);


    end;

  end loop;

  close C04;

	end;

end loop;

close C03;



if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;



end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
