and		((ie_gerar_area_sem_disp_w = 'S') or (coalesce(ie_regra_disp,'S') <> 'N'))
and		coalesce(a.ie_suspenso,'N')	<> 'S'
and		obter_se_kit_medic_sol(a.nr_prescricao, a.nr_seq_kit) = 'N'
and		exists (select	1
