CREATE OR REPLACE PROCEDURE adep_gerar_lote_fornec (nr_lote_producao_p bigint, dt_validade_p timestamp, nr_seq_horario_p bigint, nm_usuario_p text, nr_seq_lote_p INOUT bigint) AS $body$
DECLARE


dt_confirmacao_w		timestamp;
cd_material_w		integer;
cd_estabelecimento_w	smallint;
nr_seq_lote_w		bigint;
qt_material_w		double precision;
cd_cgc_w		varchar(14);
ie_tipo_descricao_w	varchar(1);
ds_lote_fornec_w	varchar(20);
nr_prescricao_w		bigint;
nr_seq_material_w	bigint;


BEGIN

select	a.cd_material,
	a.qt_unitaria,
	c.cd_estabelecimento,
	substr(obter_cgc_estabelecimento(c.cd_estabelecimento),1,14),
	coalesce(b.dt_horario, clock_timestamp()),
	a.nr_prescricao,
	a.nr_sequencia
into STRICT	cd_material_w,
	qt_material_w,
	cd_estabelecimento_w,
	cd_cgc_w,
	dt_confirmacao_w,
	nr_prescricao_w,
	nr_seq_material_w
from	prescr_mat_hor b,
	prescr_material a,
	prescr_medica c
where	b.nr_prescricao		= a.nr_prescricao
and 	b.nr_seq_material	= a.nr_sequencia
and 	b.nr_prescricao		= c.nr_prescricao
and 	a.nr_prescricao		= c.nr_prescricao
and 	b.nr_sequencia		= nr_seq_horario_p;

select	coalesce(max(obter_valor_param_usuario(143, 111, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w)),'L')
into STRICT	ie_tipo_descricao_w
;

select	nextval('material_lote_fornec_seq')
into STRICT	nr_seq_lote_w
;

ds_lote_fornec_w := substr('Prod'||nr_seq_lote_w,1,20);

if (ie_tipo_descricao_w = 'P') then
	ds_lote_fornec_w := substr('Prod'||nr_lote_producao_p,1,20);
end if;

insert into material_lote_fornec(
	nr_sequencia,
	cd_material,
	ie_origem_lote,
	nr_digito_verif,
	dt_atualizacao,
	nm_usuario,
	ds_lote_fornec,
	dt_validade,
	cd_cgc_fornec,
	qt_material,
	cd_estabelecimento,
	ie_validade_indeterminada,
	ie_situacao,
	ie_bloqueio,
	nr_lote_producao,
	dt_fabricacao,
	dt_atualizacao_nrec,
	nm_usuario_nrec)
values (	nr_seq_lote_w,
	cd_material_w,
	'L',
	calcula_digito('Modulo11', nr_seq_lote_w),
	clock_timestamp(),
	nm_usuario_p,
	ds_lote_fornec_w,
	dt_validade_p,
	cd_cgc_w,
	qt_material_w,
	cd_estabelecimento_w,
	CASE WHEN coalesce(dt_validade_p::text, '') = '' THEN 'S'  ELSE 'N' END ,
	'A',
	'N',
	null,
	dt_confirmacao_w,
	clock_timestamp(),
	nm_usuario_p);

update	prescr_material
set		nr_seq_lote_fornec	= nr_seq_lote_w
where	nr_prescricao		= nr_prescricao_w
and		nr_sequencia		= nr_seq_material_w;

update	prescr_mat_hor
set		nr_seq_lote_fornec	= nr_seq_lote_w
where	nr_sequencia		= nr_seq_horario_p;

commit;

nr_seq_lote_p	:= nr_seq_lote_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
