CREATE OR REPLACE PROCEDURE adep_gerar_ordem_apres_gas ( nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
nr_seq_apres_w		smallint := 0;

c01 CURSOR FOR
SELECT	nr_sequencia
from	w_adep_t
where	ie_tipo_item = 'O'
order by nr_sequencia;


BEGIN

open c01;
loop
fetch c01 into	nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	nr_seq_apres_w := nr_seq_apres_w + 1;

	update	w_adep_t
	set	nr_seq_apres_inf = nr_seq_apres_w
	where	nr_sequencia = nr_sequencia_w;

end;
end loop;
close c01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
