CREATE OR REPLACE PROCEDURE adep_gerar_sv_glicemia ( nr_atendimento_p bigint, qt_hgt_p bigint, nm_usuario_p text, qt_insulina_ui_p bigint, dt_glicemia_p timestamp, nr_seq_assinatura_p bigint, qt_insulina_int_ui_p bigint, ie_glic_extrapol_p text, qt_glicose_adm_p bigint, qt_hgt_mmol_p bigint default null) AS $body$
DECLARE


nr_sequencia_w		bigint;

BEGIN
if (qt_hgt_p > 0) then
	
	select	nextval('atendimento_sinal_vital_seq')
	into STRICT	nr_sequencia_w
	;

	insert into atendimento_sinal_vital(	nr_sequencia,	
						nr_atendimento,
						dt_sinal_vital,
						dt_atualizacao,
						nm_usuario,
						qt_glicemia_capilar,
						ie_situacao,
						ie_pressao,
						cd_pessoa_fisica,
						qt_insulina,
						qt_ui_insulina_int,
						ie_glic_extrapol,
						qt_glicose_adm,
						qt_glicemia_mmol,
						cd_paciente)
			values (nr_sequencia_w,
						nr_atendimento_p,
						dt_glicemia_p,
						clock_timestamp(),
						nm_usuario_p,
						qt_hgt_p,
						'A',
						'D',
						obter_dados_usuario_opcao(nm_usuario_p,'C'),
						qt_insulina_ui_p,
						qt_insulina_int_ui_p,
						ie_glic_extrapol_p,
						qt_glicose_adm_p,
						qt_hgt_mmol_p,
						obter_Pessoa_Atendimento(nr_atendimento_p,'C'));

	commit;
	CALL Liberar_Sinal_Vital(nr_sequencia_w, 'ATENDIMENTO_SINAL_VITAL', nm_usuario_p);
	
	delete from pep_item_pendente
	where	nr_seq_sinal_vital = nr_sequencia_w
	and		ie_tipo_pendencia = 'A';
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
