BEGIN
if (nr_seq_registro_p IS NOT NULL AND nr_seq_registro_p::text <> '') and (nm_tabela_p IS NOT NULL AND nm_tabela_p::text <> '') then

	ds_comando_w :=	'update	' || nm_tabela_p ||
