	;

	insert into w_adep_t(
		nr_sequencia,
		nm_usuario,
		ie_tipo_item,
		nr_prescricao,
		nr_seq_item,
		cd_item,
		ds_item,
		ie_acm_sn,
		ds_prescricao,
		ds_diluicao,
		cd_intervalo,
		ie_diferenciado,
		nr_dia_util,
		nr_seq_proc_interno,
		ie_medic_controlado,
		nr_agrupamento,
		qt_item)
	values (
		nr_seq_wadep_w,
		nm_usuario_p,
		'B',
		nr_prescricao_w,
		nr_seq_item_ordem_w,
		nr_seq_horario_w,
		ds_ordem_w,
		ie_acm_sn_w,
		ds_prescricao_w,
		ds_diluicao_w,
		'X',
		'N',
		0,
		0,
		'N',
		0,
		0);
	end;
end loop;
close c01;
end;
