SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE adep_obter_sne ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_exibir_sol_realizadas_p text, ie_exibir_sol_suspensas_p text, ie_regra_inclusao_p text, ie_data_lib_prescr_p text, ie_lib_pend_rep_p text, ie_exibir_suspensos_p text, ie_agrupar_acm_sn_p text, ie_exibe_sem_lib_farm_p text, ie_prescr_setor_p text, cd_setor_paciente_p bigint) AS $body$
DECLARE

 
nr_seq_wadep_w		bigint;
nr_prescricao_w		bigint;
nr_seq_material_w	integer;
cd_material_w		integer;
ds_material_w		varchar(255);
ie_acm_sn_w		varchar(1);
cd_intervalo_w		varchar(7);
qt_dose_w		double precision;
nr_agrupamento_w	double precision;
ds_prescricao_w		varchar(100);
ie_status_solucao_w	varchar(3);
ie_status_w		varchar(1);
ie_lib_pend_rep_w	varchar(1);
ds_dil_obs_w		varchar(2000);
ie_horario_w		varchar(1);
					
c01 CURSOR FOR 
SELECT	nr_prescricao, 
	nr_seq_material, 
	cd_material, 
	ds_material, 
	ie_acm_sn, 
	cd_intervalo, 
	qt_dose, 
	ds_prescricao, 
	ie_status_solucao, 
	ie_suspenso, 
	ds_dil_obs 
from	( 
	SELECT	a.nr_prescricao, 
		x.nr_sequencia nr_seq_material, 
		x.cd_material, 
		y.ds_material, 
		obter_se_acm_sn(x.ie_acm,x.ie_se_necessario) ie_acm_sn,		 
		x.cd_intervalo, 
		x.qt_dose, 
		substr(adep_obter_um_dosagem_prescr(a.nr_prescricao,x.nr_sequencia,x.ie_acm,x.ie_se_necessario),1,100) ds_prescricao, 
		substr(obter_status_solucao_prescr(2,a.nr_prescricao,x.nr_sequencia),1,3) ie_status_solucao, 
		coalesce(x.ie_suspenso,'N') ie_suspenso, 
		CASE WHEN coalesce(x.ds_observacao::text, '') = '' THEN  ''  ELSE OBTER_DESC_EXPRESSAO(341947)||substr(x.ds_observacao,1,1900) END  ds_dil_obs 
	from	material y, 
		prescr_material x, 
		prescr_medica a 
	where	y.cd_material = x.cd_material 
	and	x.nr_prescricao = a.nr_prescricao 
	and	obter_se_exibir_rep_adep_setor(cd_setor_paciente_p,a.cd_setor_atendimento,coalesce(a.ie_adep,'S')) = 'S' 
	and	a.nr_atendimento = nr_atendimento_p 
	and	a.dt_validade_prescr > dt_validade_limite_p 
	and	((obter_se_prescr_lib_adep(a.dt_liberacao_medico, a.dt_liberacao, a.dt_liberacao_farmacia, ie_data_lib_prescr_p) = 'S') or 
		((ie_exibe_sem_lib_farm_p = 'S') and (coalesce(a.IE_PRESCR_NUTRICAO, 'N') = 'S'))) 
	and	x.ie_agrupador = 8 
	and	((x.ie_status in ('I','INT')) or 
		 ((obter_se_acm_sn(x.ie_acm,x.ie_se_necessario) = 'N') and (x.dt_status between dt_inicial_horarios_p and dt_final_horarios_p)) or 
		 ((obter_se_acm_sn(x.ie_acm,x.ie_se_necessario) = 'S') and (obter_se_prescr_vig_adep(a.dt_inicio_prescr,a.dt_validade_prescr,dt_inicial_horarios_p,dt_final_horarios_p) = 'S'))) 
	and	((ie_exibir_sol_realizadas_p = 'S') or (x.ie_status <> 'T')) 
	and	((ie_exibir_sol_suspensas_p = 'S') or (coalesce(x.dt_suspensao::text, '') = '')) 
	and	((ie_exibir_suspensos_p = 'S') or (coalesce(x.dt_suspensao::text, '') = '')) 
	and	((ie_regra_inclusao_p = 'S') or 
		 ((ie_regra_inclusao_p = 'R') and (adep_obter_regra_inclusao(	'SNE', 
																		cd_estabelecimento_p, 
																		cd_setor_usuario_p, 
																		cd_perfil_p, 
																		x.cd_material, 
																		null, 
																		null, 
																		null, 
																		a.cd_setor_atendimento, 
																		null, 
																		null, -- nr_prescricao_p. Passei nulo porque criaram o param na adep_obter_regra_inclusao como default null, e não haviam passado nada 
																		null) = 'S'))) -- nr_seq_exame_p 
	and	((ie_prescr_setor_p = 'N') or (ie_prescr_setor_p = 'S' AND a.cd_setor_atendimento = cd_setor_paciente_p)) 
	group by 
		a.nr_prescricao, 
		x.nr_sequencia, 
		x.cd_material, 
		y.ds_material, 
		x.ie_acm, 
		x.ie_se_necessario,		 
		x.cd_intervalo, 
		x.qt_dose, 
		x.ie_suspenso, 
		x.ds_observacao, 
		CASE WHEN coalesce(x.ds_observacao::text, '') = '' THEN  ''  ELSE OBTER_DESC_EXPRESSAO(341947)||substr(x.ds_observacao,1,1900) END  
	) alias53 
group by 
	nr_prescricao, 
	nr_seq_material, 
	cd_material, 
	ds_material, 
	ie_acm_sn, 
	cd_intervalo, 
	qt_dose, 
	ds_prescricao, 
	ie_status_solucao, 
	ie_suspenso, 
	ds_dil_obs;
	
c02 CURSOR FOR 
SELECT	nr_prescricao, 
	nr_seq_material, 
	cd_material, 
	ds_material, 
	ie_acm_sn, 
	cd_intervalo, 
	qt_dose, 
	ds_prescricao, 
	ie_status_solucao, 
	ie_suspenso, 
	ie_lib_pend_rep, 
	ie_horario 
from	( 
	SELECT	a.nr_prescricao, 
		x.nr_sequencia nr_seq_material, 
		x.cd_material, 
		y.ds_material, 
		obter_se_acm_sn(x.ie_acm,x.ie_se_necessario) ie_acm_sn,		 
		x.cd_intervalo, 
		x.qt_dose, 
		substr(adep_obter_um_dosagem_prescr(a.nr_prescricao,x.nr_sequencia,x.ie_acm,x.ie_se_necessario),1,100) ds_prescricao, 
		substr(obter_status_solucao_prescr(2,a.nr_prescricao,x.nr_sequencia),1,3) ie_status_solucao, 
		coalesce(x.ie_suspenso,'N') ie_suspenso, 
		substr(adep_obter_lib_pend_rep_gestao(a.dt_liberacao_medico,a.dt_liberacao,a.dt_liberacao_farmacia),1,1) ie_lib_pend_rep, 
		CASE WHEN coalesce(x.ds_horarios::text, '') = '' THEN  'N'  ELSE 'S' END  ie_horario 
	from	material y, 
		prescr_material x, 
		prescr_medica a 
	where	y.cd_material = x.cd_material 
	and	x.nr_prescricao = a.nr_prescricao 
	and	obter_se_exibir_rep_adep_setor(cd_setor_paciente_p,a.cd_setor_atendimento,coalesce(a.ie_adep,'S')) = 'S' 
	and	a.nr_atendimento = nr_atendimento_p 
	and	a.dt_validade_prescr > dt_validade_limite_p 
	--and	obter_se_prescr_lib_adep(a.dt_liberacao_medico, a.dt_liberacao, a.dt_liberacao_farmacia, ie_data_lib_prescr_p) = 'S' 
	and	x.ie_agrupador = 8 
	and	coalesce(x.dt_status::text, '') = '' 
	and	coalesce(x.ie_status::text, '') = '' 
	and	obter_se_prescr_vig_adep(a.dt_inicio_prescr,a.dt_validade_prescr,dt_inicial_horarios_p,dt_final_horarios_p) = 'S' 
	and	((ie_exibir_sol_suspensas_p = 'S') or (coalesce(x.dt_suspensao::text, '') = '')) 
	and	((ie_exibir_suspensos_p = 'S') or (coalesce(x.dt_suspensao::text, '') = '')) 
	and	((ie_regra_inclusao_p = 'S') or 
		 ((ie_regra_inclusao_p = 'R') and (adep_obter_regra_inclusao(	'SNE', 
																		cd_estabelecimento_p, 
																		cd_setor_usuario_p, 
																		cd_perfil_p, 
																		x.cd_material, 
																		null, 
																		null, 
																		null, 
																		a.cd_setor_atendimento, 
																		null, 
																		null, -- nr_prescricao_p. Passei nulo porque criaram o param na adep_obter_regra_inclusao como default null, e não haviam passado nada 
																		null) = 'S'))) -- nr_seq_material_p 
	and	((ie_prescr_setor_p = 'N') or (ie_prescr_setor_p = 'S' AND a.cd_setor_atendimento = cd_setor_paciente_p)) 
	group by 
		a.nr_prescricao, 
		x.nr_sequencia, 
		x.cd_material, 
		y.ds_material, 
		x.ie_acm, 
		x.ie_se_necessario,		 
		x.cd_intervalo, 
		x.qt_dose, 
		x.ie_suspenso, 
		a.dt_liberacao_medico, 
		a.dt_liberacao, 
		a.dt_liberacao_farmacia, 
		x.ds_horarios 
	) alias31 
group by 
	nr_prescricao, 
	nr_seq_material, 
	cd_material, 
	ds_material, 
	ie_acm_sn, 
	cd_intervalo, 
	qt_dose, 
	ds_prescricao, 
	ie_status_solucao, 
	ie_suspenso, 
	ie_lib_pend_rep, 
	ie_horario;


BEGIN 
open c01;
loop 
fetch c01 into	nr_prescricao_w, 
		nr_seq_material_w, 
		cd_material_w, 
		ds_material_w, 
		ie_acm_sn_w,		 
		cd_intervalo_w, 
		qt_dose_w, 
		ds_prescricao_w, 
		ie_status_solucao_w, 
		ie_status_w, 
		ds_dil_obs_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	select	nextval('w_adep_t_seq') 
	into STRICT	nr_seq_wadep_w 
	;
	 
	insert into w_adep_t( 
		nr_sequencia, 
		nm_usuario, 
		ie_tipo_item, 
		nr_prescricao, 
		nr_seq_item,		 
		cd_item, 
		ds_item, 
		ie_acm_sn,		 
		cd_intervalo, 
		qt_item, 
		nr_agrupamento, 
		ds_prescricao, 
		ie_status_item, 
		nr_seq_proc_interno, 
		ie_diferenciado, 
		ds_diluicao) 
	values ( 
		nr_seq_wadep_w, 
		nm_usuario_p, 
		'SNE', 
		nr_prescricao_w, 
		nr_seq_material_w, 
		cd_material_w, 
		ds_material_w, 
		ie_acm_sn_w, 
		cd_intervalo_w, 
		qt_dose_w, 
		0, 
		ds_prescricao_w, 
		ie_status_solucao_w, 
		0, 
		'N', 
		ds_dil_obs_w);
	end;
end loop;
close c01;
 
if (ie_lib_pend_rep_p = 'S') then 
	begin 
	open c02;
	loop 
	fetch c02 into	nr_prescricao_w, 
			nr_seq_material_w, 
			cd_material_w, 
			ds_material_w, 
			ie_acm_sn_w,		 
			cd_intervalo_w, 
			qt_dose_w, 
			ds_prescricao_w, 
			ie_status_solucao_w, 
			ie_status_w, 
			ie_lib_pend_rep_w, 
			ie_horario_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		begin 
		select	nextval('w_adep_t_seq') 
		into STRICT	nr_seq_wadep_w 
		;
		 
		insert into w_adep_t( 
			nr_sequencia, 
			nm_usuario, 
			ie_tipo_item, 
			nr_prescricao, 
			nr_seq_item,		 
			cd_item, 
			ds_item, 
			ie_acm_sn,		 
			cd_intervalo, 
			qt_item, 
			nr_agrupamento, 
			ds_prescricao, 
			ie_status_item, 
			nr_seq_proc_interno, 
			ie_diferenciado, 
			ie_pendente_liberacao, 
			ie_horario) 
		values ( 
			nr_seq_wadep_w, 
			nm_usuario_p, 
			'SNE', 
			nr_prescricao_w, 
			nr_seq_material_w, 
			cd_material_w, 
			ds_material_w, 
			ie_acm_sn_w, 
			cd_intervalo_w, 
			qt_dose_w, 
			0, 
			ds_prescricao_w, 
			ie_status_solucao_w, 
			0, 
			'N', 
			ie_lib_pend_rep_w, 
			ie_horario_w);
		end;
	end loop;
	close c02;	
	end;
end if;
end;
$body$
