SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE adep_retirar_processo ( nr_processo_p bigint, nr_etiquetas_retirar_p text, nm_usuario_p text) AS $body$
DECLARE


nr_processo_w		bigint;
nr_etiquetas_retirar_w	varchar(1000);
tam_lista_w		bigint;
ie_pos_virgula_w		integer;
nr_seq_etiqueta_w		bigint;
nr_prescricao_w		bigint;
nr_seq_material_w		integer;
nr_cont_w				integer;


BEGIN
if (nr_processo_p IS NOT NULL AND nr_processo_p::text <> '') and (nr_etiquetas_retirar_p IS NOT NULL AND nr_etiquetas_retirar_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin

	nr_etiquetas_retirar_w	:= nr_etiquetas_retirar_p;

	while(nr_etiquetas_retirar_w IS NOT NULL AND nr_etiquetas_retirar_w::text <> '') loop
		begin

		tam_lista_w	:= length(nr_etiquetas_retirar_w);
		ie_pos_virgula_w	:= position(',' in nr_etiquetas_retirar_w);

		if (ie_pos_virgula_w <> 0) then
			nr_seq_etiqueta_w		:= (substr(nr_etiquetas_retirar_w,1,(ie_pos_virgula_w - 1)))::numeric;
			nr_etiquetas_retirar_w	:= substr(nr_etiquetas_retirar_w,(ie_pos_virgula_w + 1),tam_lista_w);
		else
			nr_seq_etiqueta_w		:= (nr_etiquetas_retirar_w)::numeric;
			nr_etiquetas_retirar_w	:= null;
		end if;

		if (nr_seq_etiqueta_w > 0) then

			select	nr_prescricao,
				nr_seq_material
			into STRICT	nr_prescricao_w,
				nr_seq_material_w
			from	prescr_mat_hor
			where	nr_sequencia	= nr_seq_etiqueta_w
			and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';

			update	prescr_mat_hor
			set	nr_seq_processo	 = NULL,
				nr_seq_etiqueta	 = NULL,
          		nr_seq_etiqueta_reaprov  = NULL
			where	nr_sequencia	= nr_seq_etiqueta_w;

			update	prescr_mat_hor
			set	nr_seq_processo	 = NULL,
				nr_seq_etiqueta	 = NULL,
          		nr_seq_etiqueta_reaprov  = NULL
			where	nr_seq_processo	= nr_processo_p
			and	nr_prescricao	= nr_prescricao_w
			and	nr_seq_superior	= nr_seq_material_w;
		end if;

		end;
	end loop;

	select 	count(nr_sequencia)
	into STRICT	nr_cont_w
	from	prescr_mat_hor
	where	nr_seq_processo	= nr_processo_p;

	if (nr_cont_w = 0) then
		CALL desfazer_processo_adep(nr_processo_p, 'S', nm_usuario_p);
	end if;

	exception
		when others then
		rollback;
		/*Ocorreu uma exceção ao retirar os itens do processo!' || chr(10) ||
						'PROCESSO=' || to_char(nr_processo_p) || chr(10) ||
						'ETIQUETAS=' || nr_etiquetas_retirar_p || '#@#@');*/
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(261447,'PROCESSO='||to_char(nr_processo_p) || ';' ||
					'ETIQUETA=' || nr_etiquetas_retirar_p);
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE adep_retirar_processo ( nr_processo_p bigint, nr_etiquetas_retirar_p text, nm_usuario_p text) FROM PUBLIC;

