SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE adep_retirar_susp_processo ( nr_processo_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_processo_w		bigint;
nr_etiquetas_retirar_w	varchar(1000);
tam_lista_w		bigint;
ie_pos_virgula_w		integer;
nr_seq_etiqueta_w		bigint;
nr_prescricao_w		bigint;
nr_seq_material_w		integer;

c01 CURSOR FOR
SELECT	nr_sequencia,
	nr_prescricao,
	nr_seq_material
from	prescr_mat_hor
where	nr_seq_processo	= nr_processo_p
and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S'
and	(dt_suspensao IS NOT NULL AND dt_suspensao::text <> '')
and	coalesce(nr_seq_superior::text, '') = ''
order by
	nr_sequencia;


BEGIN
if (nr_processo_p IS NOT NULL AND nr_processo_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin

	open c01;
	loop
	fetch c01 into	nr_seq_etiqueta_w,
			nr_prescricao_w,
			nr_seq_material_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		update	prescr_mat_hor
		set	nr_seq_processo	 = NULL
		where	nr_sequencia	= nr_seq_etiqueta_w;

		update	prescr_mat_hor
		set	nr_seq_processo	 = NULL
		where	nr_seq_processo	= nr_processo_p
		and	nr_prescricao	= nr_prescricao_w
		and	nr_seq_superior	= nr_seq_material_w;


		end;
	end loop;
	close c01;

	exception
		when others then
		rollback;
		/*'Ocorreu uma exceção ao retirar os itens suspensos do processo!' || chr(10) ||
						'PROCESSO=' || to_char(nr_processo_p) || '#@#@');*/
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(261452, 'PROCESSO='||to_char(nr_processo_p));
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
