CREATE OR REPLACE PROCEDURE adep_sincronizar_jejum ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_exibir_hor_realizados_p text, ie_exibir_hor_suspensos_p text, ie_regra_inclusao_p text, ie_data_lib_prescr_p text, ie_exibir_suspensos_p text, dt_horario_p timestamp, nr_horario_p integer, ie_exibe_sem_lib_farm_p text, ie_prescr_setor_p text, cd_setor_paciente_p bigint) AS $body$
DECLARE


ds_sep_bv_w		varchar(50);
nr_prescricao_w		bigint;
cd_jejum_w		varchar(255);
ds_jejum_w		varchar(255);
ds_vigencia_w		varchar(255);
ds_comando_update_w	varchar(4000);

c01 CURSOR FOR
SELECT	a.nr_prescricao,
	to_char(c.dt_inicio,'dd/mm/yyyy hh24:mi:ss') || '-' || to_char(c.dt_fim,'dd/mm/yyyy hh24:mi:ss') cd_jejum,
	x.ds_tipo ds_jejum,
	'Vigência: ' || to_char(c.dt_inicio,'dd/mm/yyyy hh24:mi:ss') || ' à ' || to_char(c.dt_fim,'dd/mm/yyyy hh24:mi:ss') ds_vigencia
from	rep_tipo_jejum x,
	rep_jejum c,
	prescr_medica a
where	x.nr_sequencia = c.nr_seq_tipo
and	c.nr_prescricao = a.nr_prescricao
and	obter_se_exibir_rep_adep_setor(cd_setor_paciente_p,a.cd_setor_atendimento,coalesce(a.ie_adep,'S')) = 'S'
and	a.nr_atendimento = nr_atendimento_p
and	a.dt_validade_prescr > dt_validade_limite_p
and	((obter_se_prescr_lib_adep(a.dt_liberacao_medico, a.dt_liberacao, a.dt_liberacao_farmacia, ie_data_lib_prescr_p) = 'S') or
	((ie_exibe_sem_lib_farm_p = 'S') and (coalesce(a.IE_PRESCR_NUTRICAO, 'N') = 'S')))
and	((ie_exibir_suspensos_p = 'S') or (coalesce(c.ie_suspenso,'N') = 'N'))
and	c.dt_inicio = dt_horario_p
and	((ie_prescr_setor_p = 'N') or (ie_prescr_setor_p = 'S' AND a.cd_setor_atendimento = cd_setor_paciente_p))
group by
	a.nr_prescricao,
	c.dt_inicio,
	c.dt_fim,
	x.ds_tipo;


BEGIN
ds_sep_bv_w := obter_separador_bv;
open c01;
loop
fetch c01 into	nr_prescricao_w,
		cd_jejum_w,
		ds_jejum_w,
		ds_vigencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	ds_comando_update_w	:=	' update w_adep_t ' ||
					' set nr_prescricoes = adep_juntar_prescricao(nr_prescricoes,:nr_prescricao) ' ||
					' where nm_usuario = :nm_usuario ' ||
					' and ie_tipo_item = :ie_tipo ' ||
					' and cd_item = :cd_item ' ||
					' and ((ds_prescricao = :ds_vigencia) or (ds_prescricao is null)) ';

	CALL exec_sql_dinamico_bv('ADEP', ds_comando_update_w,	'nr_prescricao=' || to_char(nr_prescricao_w) || ds_sep_bv_w ||
								'nm_usuario=' || nm_usuario_p || ds_sep_bv_w ||
								'ie_tipo=J' || ds_sep_bv_w ||
								'cd_item=' || cd_jejum_w || ds_sep_bv_w ||
								'ds_vigencia=' || ds_vigencia_w || ds_sep_bv_w );
	end;
end loop;
close c01;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
