CREATE OR REPLACE PROCEDURE adicionar_etapa_barras_key ( ie_conta_etapa_p text, nr_interno_conta_p bigint, nr_atendimento_p bigint, nr_sequencia_etapa_p bigint, cd_setor_atendimento_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ds_mensagem_p INOUT text, nm_paciente_p INOUT text, nr_atend_out_p INOUT bigint) AS $body$
DECLARE

 
nr_atendimento_w		bigint;
cd_convenio_w		integer;
ie_etapa_usuario_w		varchar(1);
nm_paciente_w		varchar(60);


BEGIN 
 
nr_atend_out_p	:= nr_atendimento_p;
 
if (ie_conta_etapa_p = 'S') then 
 
	select	coalesce(max(nr_atendimento),0) nr_atendimento, 
		coalesce(max(cd_convenio_parametro),0) cd_convenio 
	into STRICT	nr_atendimento_w, 
		cd_convenio_w 
	from	conta_paciente 
	where	nr_interno_conta = nr_interno_conta_p;
	 
	nr_atend_out_p := nr_atendimento_w;
else	 
	select	obter_convenio_atendimento(nr_atendimento_p) cd_convenio 
	into STRICT	cd_convenio_w 
	;
end if;
 
select	max(obter_liber_etapa_usuario(nr_sequencia_etapa_p, cd_estabelecimento_p, nm_usuario_p,cd_setor_atendimento_p, cd_convenio_w)) 
into STRICT	ie_etapa_usuario_w
;
 
if (ie_etapa_usuario_w = 'N') then 
	ds_mensagem_p := obter_texto_dic_objeto(157172, wheb_usuario_pck.get_nr_seq_idioma, null);
end if;
 
select	max(obter_dados_atendimento(nr_atend_out_p,'NP')) 
into STRICT	nm_paciente_w
;
 
nm_paciente_p := nm_paciente_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
