CREATE OR REPLACE PROCEDURE adicionar_kit_registro_pda ( nm_usuario_p text, cd_kit_material_p bigint, nr_seq_reg_kit_p bigint, cd_local_estoque_p bigint) AS $body$
DECLARE


cd_estabelecimento_w	smallint;
cd_material_w		integer;
nr_seq_kit_w		bigint;
ds_erro_w		varchar(2000);


BEGIN

select	max(cd_estabelecimento)
into STRICT	cd_estabelecimento_w
from	kit_estoque_reg
where	nr_sequencia = nr_seq_reg_kit_p;

select	nextval('kit_estoque_seq')
into STRICT	nr_seq_kit_w
;

insert 	into kit_estoque(
	cd_estabelecimento,
	cd_kit_material,
	cd_local_estoque,
	dt_atualizacao,
	nm_usuario,
	nr_seq_reg_kit,
	nr_sequencia,
	dt_montagem,
	nm_usuario_montagem)
values (	cd_estabelecimento_w,
	cd_kit_material_p,
	cd_local_estoque_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_reg_kit_p,
	nr_seq_kit_w,
	clock_timestamp(),
	nm_usuario_p);

ds_erro_w := gera_componente_kit_mat(cd_kit_material_p, nr_seq_kit_w, 'A', 'N', nm_usuario_p, ds_erro_w);

CALL sup_gravar_historico_reg_kit(nm_usuario_p,nr_seq_reg_kit_p,'P','I',wheb_mensagem_pck.get_texto(313623)||' ' || nr_seq_kit_w || ' '|| wheb_mensagem_pck.get_texto(313624)|| ' ' || nr_seq_reg_kit_p || '.');

commit;

end;
$body$
