CREATE OR REPLACE PROCEDURE adicionar_observacao_agenda ( ds_observacao_p text, nr_seq_agenda_p bigint, nm_usuario_p text ) AS $body$
BEGIN

if	(nr_seq_agenda_p > 0 AND ds_observacao_p IS NOT NULL AND ds_observacao_p::text <> '') then

	update	agenda_consulta
	set	ds_observacao	=	substr(substr(ds_observacao,1,1900) || chr(13) || chr(10) || substr(ds_observacao_p,1,1900),1,2000),
		nm_usuario	=	nm_usuario_p,
		dt_atualizacao	=	clock_timestamp()
	where	nr_sequencia	=	nr_seq_agenda_p;
	commit;
end if;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
